import React, { useState } from "react";
import "./style.css";
import Menu from "./menuApi.js";
import MenuCard from "./MenuCard";
//import Navbar from "./Navbar";

/*const uniqueList = [
  ...new Set(
    Menu.map((curElem) => {
      return curElem.category;
    })
  ),
  "All",
];

console.log(uniqueList);*/

const Restaurant = () => {

  const [menuData, setMenuData] = useState(Menu);
  //const [menuList, setMenuList] = useState(uniqueList);

  const filterItem = (category) => {
    /*if (category === "All") {
      setMenuData(Menu);
      return;
    }
*/
    const updatedList = Menu.filter((curElem) => {
      return curElem.category === category;
    });

    setMenuData(updatedList);
  };


  return (
    <>
      <nav className="navbar">
        <div className="btn-group">
          <button className="btn-group_item" onClick={() => filterItem("lunch")}>
            Lunch </button>
          <button className="btn-group_item" onClick={() => filterItem("evening")}>
            Evening </button>
          <button className="btn-group_item" onClick={() => filterItem("breakfast")}>
            Breakfast </button>
          <button className="btn-group_item" onClick={() => filterItem("dinner")}>
            Dinner </button>
          <button className="btn-group_item" onClick={() => filterItem("all")}>
            All </button>
        </div>
      </nav>
      {/*<Navbar filterItem={filterItem} menuList={menuList} />*/}
      <MenuCard menuData={menuData} />
    </>
  );
};

export default Restaurant;
